package io.jenkins.plugins.explain_error;

import hudson.Extension;
import hudson.model.Descriptor;
import hudson.util.FormValidation;
import hudson.util.ListBoxModel;
import hudson.util.Secret;
import jenkins.model.GlobalConfiguration;
import jenkins.model.Jenkins;
import net.sf.json.JSONObject;
import org.kohsuke.stapler.DataBoundSetter;
import org.kohsuke.stapler.QueryParameter;
import org.kohsuke.stapler.StaplerRequest2;
import org.kohsuke.stapler.interceptor.RequirePOST;
import org.jenkinsci.Symbol;
import java.io.IOException;
import java.util.logging.Level;
import java.util.logging.Logger;


/**
 * Global configuration for the plugin.
 */
@Extension
@Symbol("explainError")
public class GlobalConfigurationImpl extends GlobalConfiguration {

    private Secret apiKey;
    private AIProvider provider = AIProvider.OPENAI;
    private String apiUrl;
    private String model;
    private boolean enableExplanation = true;

    public GlobalConfigurationImpl() {
        load();
    }

    /**
     * Get the singleton instance of GlobalConfigurationImpl.
     * @return the GlobalConfigurationImpl instance
     */
    public static GlobalConfigurationImpl get() {
        return Jenkins.get().getDescriptorByType(GlobalConfigurationImpl.class);
    }

    @Override
    public boolean configure(StaplerRequest2 req, JSONObject json) throws Descriptor.FormException {
        try {
            // Validate required fields before binding
            if (json.has("enableExplanation")) {
                this.enableExplanation = json.getBoolean("enableExplanation");
            }

            if (json.has("provider")) {
                String providerStr = json.getString("provider");
                try {
                    this.provider = AIProvider.valueOf(providerStr);
                } catch (IllegalArgumentException e) {
                    throw new Descriptor.FormException("Invalid provider: " + providerStr, "provider");
                }
            }

            if (json.has("apiKey")) {
                String apiKeyStr = json.getString("apiKey");
                this.apiKey = Secret.fromString(apiKeyStr);
            }

            if (json.has("apiUrl")) {
                this.apiUrl = json.getString("apiUrl");
            }

            if (json.has("model")) {
                this.model = json.getString("model");
            }

            save();
            return true;
        } catch (Exception e) {
            Logger.getLogger(GlobalConfigurationImpl.class.getName()).log(Level.SEVERE, "Configuration failed", e);
            throw new Descriptor.FormException("Configuration failed: " + e.getMessage(), e, "");
        }
    }

    // Getters and setters
    public Secret getApiKey() {
        return apiKey;
    }

    @DataBoundSetter
    public void setApiKey(Secret apiKey) {
        this.apiKey = apiKey;
    }

    public AIProvider getProvider() {
        return provider != null ? provider : AIProvider.OPENAI;
    }

    @DataBoundSetter
    public void setProvider(AIProvider provider) {
        this.provider = provider;
    }

    public String getApiUrl() {
        return apiUrl;
    }

    /**
     * Get the raw configured API URL without defaults, used for validation.
     */
    public String getRawApiUrl() {
        return apiUrl;
    }

    @DataBoundSetter
    public void setApiUrl(String apiUrl) {
        this.apiUrl = apiUrl;
    }

    public String getModel() {
        return model;
    }

    /**
     * Get the raw configured model without defaults, used for validation.
     */
    public String getRawModel() {
        return model;
    }

    @DataBoundSetter
    public void setModel(String model) {
        this.model = model;
    }

    public boolean isEnableExplanation() {
        return enableExplanation;
    }

    @DataBoundSetter
    public void setEnableExplanation(boolean enableExplanation) {
        this.enableExplanation = enableExplanation;
    }

    @Override
    public String getDisplayName() {
        return "Explain Error Plugin Configuration";
    }

    /**
     * Get all available AI providers for the dropdown.
     */
    public AIProvider[] getProviderValues() {
        return AIProvider.values();
    }

    /**
     * Populate the provider dropdown items for the UI.
     */
    @RequirePOST
    public ListBoxModel doFillProviderItems() {
        Jenkins.get().checkPermission(Jenkins.ADMINISTER);

        ListBoxModel model = new ListBoxModel();
        AIProvider currentProvider = getProvider(); // Get the current provider

        for (AIProvider p : AIProvider.values()) {
            model.add(new ListBoxModel.Option(
                p.getDisplayName(),          // display name
                p.name(),                    // actual value
                p == currentProvider         // is selected
            ));
        }

        return model;
}

    /**
     * Method to test the AI API configuration.
     * This is called when the "Test Configuration" button is clicked.
     */
    @RequirePOST
    public FormValidation doTestConfiguration(@QueryParameter("apiKey") String apiKey,
                                                @QueryParameter("provider") String provider,
                                                @QueryParameter("apiUrl") String apiUrl,
                                                @QueryParameter("model") String model) {
        Jenkins.get().checkPermission(Jenkins.ADMINISTER);

        // Validate only the provided parameters
        Secret testApiKeySecret = (apiKey != null) ? Secret.fromString(apiKey) : null;
        AIProvider testProvider = null;
        if (provider != null && !provider.isEmpty()) {
            try {
                testProvider = AIProvider.valueOf(provider);
            } catch (IllegalArgumentException e) {
                return FormValidation.error("Invalid provider: " + provider);
            }
        }
        String testApiUrl = apiUrl != null ? apiUrl : "";
        String testModel = model != null ? model : "";

        try {
            GlobalConfigurationImpl tempConfig = new GlobalConfigurationImpl();
            tempConfig.setApiKey(testApiKeySecret);
            if (testProvider != null) {
                tempConfig.setProvider(testProvider);
            }
            tempConfig.setApiUrl(testApiUrl);
            tempConfig.setModel(testModel);

            AIService aiService = new AIService(tempConfig);
            String testResponse = aiService.explainError("Test configuration call - please respond with 'Configuration test successful'");

            if (testResponse != null && testResponse.contains("Configuration test successful")) {
                return FormValidation.ok("Configuration test successful! API connection is working properly.");
            } else if (testResponse != null && testResponse.contains("AI API Error:")) {
                return FormValidation.error("" + testResponse);
            } else if (testResponse != null && testResponse.contains("Failed to get explanation from AI service")) {
                return FormValidation.error("" + testResponse);
            } else {
                return FormValidation.ok("API connection established, but got unexpected response: " + testResponse);
            }

        } catch (IOException e) {
            Logger.getLogger(GlobalConfigurationImpl.class.getName()).log(Level.WARNING, "API test failed", e);
            return FormValidation.error("Connection failed: " + e.getMessage() + ". Please check your API URL and network connection.");
        } catch (Exception e) {
            Logger.getLogger(GlobalConfigurationImpl.class.getName()).log(Level.WARNING, "Configuration test failed", e);
            return FormValidation.error("Test failed: " + e.getMessage());
        }
    }
}

package io.jenkins.plugins.explain_error;

/**
 * Enum representing the supported AI providers.
 */
public enum AIProvider {
    OPENAI("OpenAI", "https://api.openai.com/v1/chat/completions", "gpt-3.5-turbo"),
    GEMINI("Google Gemini", "https://generativelanguage.googleapis.com/v1beta/models/{model}:generateContent", "gemini-1.5-flash");

    private final String displayName;
    private final String defaultApiUrl;
    private final String defaultModel;

    AIProvider(String displayName, String defaultApiUrl, String defaultModel) {
        this.displayName = displayName;
        this.defaultApiUrl = defaultApiUrl;
        this.defaultModel = defaultModel;
    }

    public String getDisplayName() {
        return displayName;
    }

    public String getDefaultApiUrl() {
        return defaultApiUrl;
    }

    public String getDefaultModel() {
        return defaultModel;
    }

    @Override
    public String toString() {
        return displayName;
    }
}

package io.jenkins.plugins.explain_error;

import java.io.IOException;
import java.util.logging.Logger;

/**
 * Service class for communicating with AI APIs.
 * Factory that creates appropriate AI service implementation based on the configured provider.
 */
public class AIService {

    private static final Logger LOGGER = Logger.getLogger(AIService.class.getName());

    private final BaseAIService delegate;

    public AIService(GlobalConfigurationImpl config) {
        this.delegate = createServiceForProvider(config);
    }

    /**
     * Create the appropriate AI service implementation based on the provider.
     */
    private BaseAIService createServiceForProvider(GlobalConfigurationImpl config) {
        AIProvider provider = config.getProvider();

        switch (provider) {
            case OPENAI:
                return new OpenAIService(config);
            case GEMINI:
                return new GeminiService(config);
            default:
                LOGGER.warning("Unknown AI provider: " + provider + ". Defaulting to OpenAI.");
                return new OpenAIService(config);
        }
    }

    /**
     * Explain error logs using the configured AI provider.
     * @param errorLogs the error logs to explain
     * @return the AI explanation
     * @throws IOException if there's a communication error
     */
    public String explainError(String errorLogs) throws IOException {
        return delegate.explainError(errorLogs);
    }
}

package io.jenkins.plugins.explain_error;

import com.fasterxml.jackson.databind.ObjectMapper;
import hudson.ProxyConfiguration;
import java.io.IOException;
import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;
import java.time.Duration;
import java.util.logging.Logger;
import org.apache.commons.lang3.StringUtils;

/**
 * Base class for AI service implementations.
 * Provides common functionality for different AI providers.
 */
public abstract class BaseAIService {

    protected static final Logger LOGGER = Logger.getLogger(BaseAIService.class.getName());
    protected static final ObjectMapper MAPPER = new ObjectMapper();

    protected final GlobalConfigurationImpl config;

    public BaseAIService(GlobalConfigurationImpl config) {
        this.config = config;
    }

    /**
     * Explain error logs using the configured AI provider.
     * @param errorLogs the error logs to explain
     * @return the AI explanation
     * @throws IOException if there's a communication error
     */
    public String explainError(String errorLogs) throws IOException {
        if (StringUtils.isBlank(errorLogs)) {
            return "No error logs provided for explanation.";
        }

        String prompt = buildPrompt(errorLogs);
        String requestBody = buildRequestBody(prompt);

        try {
            URI apiUri = URI.create(getApiUrl());

            // Use Jenkins' ProxyConfiguration.newHttpRequestBuilder() to get a properly 
            // configured HttpRequest that respects Jenkins proxy settings
            HttpRequest.Builder requestBuilder = ProxyConfiguration.newHttpRequestBuilder(apiUri);

            // Build the HTTP request with proper proxy configuration
            HttpRequest request = buildHttpRequest(requestBuilder, requestBody);

            // Create HttpClient with timeout configuration
            HttpClient client = HttpClient.newBuilder()
                .connectTimeout(Duration.ofSeconds(30))
                .build();

            // Execute the request
            HttpResponse<String> response = client.send(request, HttpResponse.BodyHandlers.ofString());
            String responseBody = response.body();

            LOGGER.fine("Response body length: " + responseBody.length());
            LOGGER.fine("Response body preview: " + responseBody.substring(0, Math.min(500, responseBody.length())));

            if (response.statusCode() != 200) {
                LOGGER.severe("AI API request failed with status " + response.statusCode() + ": " + responseBody);
                return "Failed to get explanation from AI service. Status: " + response.statusCode() 
                    + ". Please check your API configuration and key.";
            }

            return parseResponse(responseBody);

        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            LOGGER.severe("AI API request was interrupted: " + e.getMessage());
            return "Request was interrupted: " + e.getMessage();
        } catch (Exception e) {
            LOGGER.severe("AI API request failed: " + e.getMessage());
            return "Failed to communicate with AI service: " + e.getMessage();
        }
    }

    /**
     * Build the prompt for the AI service.
     */
    protected String buildPrompt(String errorLogs) {
        return "You are an expert Jenkins administrator and software engineer. "
                + "Please analyze the following Jenkins build error logs and provide a clear, "
                + "actionable explanation of what went wrong and how to fix it:\n\n"
                + "ERROR LOGS:\n"
                + errorLogs + "\n\n" + "Please provide:\n"
                + "1. A summary of what caused the error\n"
                + "2. Specific steps to resolve the issue\n"
                + "3. Any relevant best practices to prevent similar issues\n\n"
                + "Keep your response concise and focused on actionable solutions.";
    }

    /**
     * Get the API URL, potentially with model substitution for providers that need it.
     */
    protected String getApiUrl() {
        String url = config.getApiUrl();
        if (url.contains("{model}")) {
            url = url.replace("{model}", config.getModel());
        }
        return url;
    }

    /**
     * Build the HTTP request for the specific AI provider.
     */
    protected abstract HttpRequest buildHttpRequest(HttpRequest.Builder requestBuilder, String requestBody);

    /**
     * Build the request body for the specific AI provider.
     */
    protected abstract String buildRequestBody(String prompt) throws IOException;

    /**
     * Parse the response from the specific AI provider.
     */
    protected abstract String parseResponse(String responseBody) throws IOException;
}

package io.jenkins.plugins.explain_error;

import hudson.Extension;
import hudson.model.Run;
import hudson.model.TaskListener;
import java.util.Set;
import org.jenkinsci.plugins.workflow.steps.Step;
import org.jenkinsci.plugins.workflow.steps.StepContext;
import org.jenkinsci.plugins.workflow.steps.StepDescriptor;
import org.jenkinsci.plugins.workflow.steps.StepExecution;
import org.jenkinsci.plugins.workflow.steps.SynchronousNonBlockingStepExecution;
import org.kohsuke.stapler.DataBoundConstructor;
import org.kohsuke.stapler.DataBoundSetter;

/**
 * Pipeline step to explain errors using AI.
 */
public class ExplainErrorStep extends Step {

    private String logPattern;
    private int maxLines;

    @DataBoundConstructor
    public ExplainErrorStep() {
        this.logPattern = "";
        this.maxLines = 100;
    }

    public String getLogPattern() {
        return logPattern;
    }

    @DataBoundSetter
    public void setLogPattern(String logPattern) {
        this.logPattern = logPattern != null ? logPattern : "";
    }

    public int getMaxLines() {
        return maxLines;
    }

    @DataBoundSetter
    public void setMaxLines(int maxLines) {
        this.maxLines = maxLines > 0 ? maxLines : 100;
    }

    @Override
    public StepExecution start(StepContext context) throws Exception {
        return new ExplainErrorStepExecution(context, this);
    }

    @Extension
    public static class DescriptorImpl extends StepDescriptor {

        @Override
        public Set<? extends Class<?>> getRequiredContext() {
            return Set.of(Run.class, TaskListener.class);
        }

        @Override
        public String getFunctionName() {
            return "explainError";
        }

        @Override
        public String getDisplayName() {
            return "Explain Error with AI";
        }
    }

    private static class ExplainErrorStepExecution extends SynchronousNonBlockingStepExecution<Void> {

        private static final long serialVersionUID = 1L;
        private final transient ExplainErrorStep step;

        ExplainErrorStepExecution(StepContext context, ExplainErrorStep step) {
            super(context);
            this.step = step;
        }

        @Override
        protected Void run() throws Exception {
            Run<?, ?> run = getContext().get(Run.class);
            TaskListener listener = getContext().get(TaskListener.class);

            // Add console explain error action to build (if not already present)
            if (run.getAction(ConsoleExplainErrorAction.class) == null) {
                run.addOrReplaceAction(new ConsoleExplainErrorAction(run));
            }

            ErrorExplainer explainer = new ErrorExplainer();
            explainer.explainError(run, listener, step.getLogPattern(), step.getMaxLines());

            return null;
        }
    }
}

package io.jenkins.plugins.explain_error;

import hudson.model.Run;
import hudson.model.TaskListener;
import java.io.IOException;
import java.util.List;
import java.util.logging.Logger;
import java.util.regex.Pattern;
import org.apache.commons.lang3.StringUtils;

/**
 * Service class responsible for explaining errors using AI.
 */
public class ErrorExplainer {

    private static final Logger LOGGER = Logger.getLogger(ErrorExplainer.class.getName());

    public void explainError(Run<?, ?> run, TaskListener listener, String logPattern, int maxLines) {
        try {
            GlobalConfigurationImpl config = GlobalConfigurationImpl.get();

            if (!config.isEnableExplanation()) {
                listener.getLogger().println("AI error explanation is disabled in global configuration.");
                return;
            }

            if (config.getApiKey() == null || StringUtils.isBlank(config.getApiKey().getPlainText())) {
                listener.getLogger()
                        .println("ERROR: API key is not configured. Please configure it in Jenkins global settings.");
                return;
            }

            // Extract error logs
            String errorLogs = extractErrorLogs(run, logPattern, maxLines);

            if (StringUtils.isBlank(errorLogs)) {
                listener.getLogger().println("No error logs found to explain.");
                return;
            }

            // Get AI explanation
            AIService aiService = new AIService(config);
            String explanation = aiService.explainError(errorLogs);

            // Store explanation in build action
            ErrorExplanationAction action = new ErrorExplanationAction(explanation, errorLogs);
            run.addOrReplaceAction(action);

            // Explanation is now available on the job page, no need to clutter console output

        } catch (Exception e) {
            LOGGER.severe("Failed to explain error: " + e.getMessage());
            listener.getLogger().println("Failed to explain error: " + e.getMessage());
        }
    }

    private String extractErrorLogs(Run<?, ?> run, String logPattern, int maxLines) throws IOException {
        List<String> logLines = run.getLog(maxLines);

        if (StringUtils.isBlank(logPattern)) {
            // Return last few lines if no pattern specified
            return String.join("\n", logLines);
        }

        Pattern pattern = Pattern.compile(logPattern, Pattern.CASE_INSENSITIVE);
        StringBuilder errorLogs = new StringBuilder();

        for (String line : logLines) {
            if (pattern.matcher(line).find()) {
                errorLogs.append(line).append("\n");
            }
        }

        return errorLogs.toString();
    }

    /**
     * Explains error text directly without extracting from logs.
     * Used for console output error explanation.
     */
    public String explainErrorText(String errorText, Run<?, ?> run) {

        try {
            GlobalConfigurationImpl config = GlobalConfigurationImpl.get();

            if (!config.isEnableExplanation()) {
                LOGGER.warning("AI error explanation is disabled in global configuration");
                return "AI error explanation is disabled in global configuration.";
            }

            if (config.getApiKey() == null || StringUtils.isBlank(config.getApiKey().getPlainText())) {
                LOGGER.warning("API key is not configured");
                return "ERROR: API key is not configured. Please configure it in Jenkins global settings.";
            }

            if (StringUtils.isBlank(errorText)) {
                LOGGER.warning("No error text provided");
                return "No error text provided to explain.";
            }

            // Get AI explanation
            AIService aiService = new AIService(config);
            String explanation = aiService.explainError(errorText);

            LOGGER.fine("Explanation length: " + (explanation != null ? explanation.length() : 0));

            return explanation;

        } catch (Exception e) {
            LOGGER.severe("Failed to explain error text: " + e.getMessage());
            e.printStackTrace();
            return "Failed to explain error: " + e.getMessage();
        }
    }
}

package io.jenkins.plugins.explain_error;

import com.fasterxml.jackson.databind.ObjectMapper;
import hudson.model.Action;
import hudson.model.Run;
import java.io.IOException;
import java.io.PrintWriter;
import java.util.logging.Logger;
import javax.servlet.ServletException;
import org.kohsuke.stapler.StaplerRequest2;
import org.kohsuke.stapler.StaplerResponse2;
import org.kohsuke.stapler.interceptor.RequirePOST;

/**
 * Action to add "Explain Error" functionality to console output pages.
 * This action needs to be manually added to builds.
 */
public class ConsoleExplainErrorAction implements Action {

    private static final Logger LOGGER = Logger.getLogger(ConsoleExplainErrorAction.class.getName());

    private final Run<?, ?> run;

    public ConsoleExplainErrorAction(Run<?, ?> run) {
        this.run = run;
    }

    @Override
    public String getIconFileName() {
        return null; // No icon in sidebar - this is for AJAX functionality only
    }

    @Override
    public String getDisplayName() {
        return null; // No display name in sidebar
    }

    @Override
    public String getUrlName() {
        return "console-explain-error";
    }

    /**
     * AJAX endpoint to explain error from console output.
     * Called via JavaScript from the console output page.
     */
    @RequirePOST
    public void doExplainConsoleError(StaplerRequest2 req, StaplerResponse2 rsp) throws ServletException, IOException {
        try {
            run.checkPermission(hudson.model.Item.READ);

            // Check if user wants to force a new explanation
            boolean forceNew = "true".equals(req.getParameter("forceNew"));

            // Check if an explanation already exists
            ErrorExplanationAction existingAction = run.getAction(ErrorExplanationAction.class);
            if (!forceNew && existingAction != null && existingAction.hasValidExplanation()) {
                // Return existing explanation with a flag indicating it's cached
                writeJsonResponse(rsp, createCachedResponse(existingAction.getExplanation()));
                return;
            }

            // Optionally allow maxLines as a parameter, default to 200
            int maxLines = 200;
            String maxLinesParam = req.getParameter("maxLines");
            if (maxLinesParam != null) {
                try { maxLines = Integer.parseInt(maxLinesParam); } catch (NumberFormatException ignore) {}
            }

            // Fetch the last N lines of the log
            java.util.List<String> logLines = run.getLog(maxLines);
            String errorText = String.join("\n", logLines);

            ErrorExplainer explainer = new ErrorExplainer();
            String explanation = explainer.explainErrorText(errorText, run);

            if (explanation != null && !explanation.trim().isEmpty()) {
                // Save the explanation as a build action (like the sidebar functionality)
                ErrorExplanationAction action = new ErrorExplanationAction(explanation, errorText);
                run.addOrReplaceAction(action);

                writeJsonResponse(rsp, explanation);
            } else {
                writeJsonResponse(rsp, "Error: Could not generate explanation. Please check your AI API configuration.");
            }
        } catch (Exception e) {
            LOGGER.severe("=== EXPLAIN ERROR REQUEST FAILED ===");
            LOGGER.severe("Error explaining console error: " + e.getMessage());
            writeJsonResponse(rsp, "Error: " + e.getMessage());
        }
    }

    /**
     * AJAX endpoint to check if an explanation already exists.
     * Returns JSON with hasExplanation boolean and timestamp if it exists.
     */
    @RequirePOST
    public void doCheckExistingExplanation(StaplerRequest2 req, StaplerResponse2 rsp) throws ServletException, IOException {
        try {
            run.checkPermission(hudson.model.Item.READ);

            ErrorExplanationAction existingAction = run.getAction(ErrorExplanationAction.class);
            boolean hasExplanation = existingAction != null && existingAction.hasValidExplanation();

            rsp.setContentType("application/json");
            rsp.setCharacterEncoding("UTF-8");
            PrintWriter writer = rsp.getWriter();

            if (hasExplanation) {
                String response = String.format(
                    "{\"hasExplanation\": true, \"timestamp\": \"%s\"}", 
                    existingAction.getFormattedTimestamp()
                );
                writer.write(response);
            } else {
                writer.write("{\"hasExplanation\": false}");
            }

            writer.flush();
        } catch (Exception e) {
            LOGGER.severe("Error checking existing explanation: " + e.getMessage());
            rsp.setStatus(500);
        }
    }

    private void writeJsonResponse(StaplerResponse2 rsp, String message) throws IOException {
        rsp.setContentType("application/json");
        rsp.setCharacterEncoding("UTF-8");
        PrintWriter writer = rsp.getWriter();

        try {
            ObjectMapper mapper = new ObjectMapper();
            String jsonResponse = mapper.writeValueAsString(message);
            writer.write(jsonResponse);
        } catch (Exception e) {
            // Fallback to simple JSON string
            writer.write("\"" + message.replace("\"", "\\\"") + "\"");
        }
        writer.flush();
    }

    /**
     * Create a response indicating this is a cached result.
     * @param explanation The cached explanation
     * @return The response string with cached indicator
     */
    private String createCachedResponse(String explanation) {
        return explanation + "\n\n[Note: This is a previously generated explanation. Use the 'Generate New' option to create a new one.]";
    }

    public Run<?, ?> getRun() {
        return run;
    }
}

document.addEventListener('DOMContentLoaded', function () {
  if (
    window.location.pathname.match(/\/console(Full)?$/) &&
    !window.location.pathname.includes('/error-explanation')
  ) {
    addExplainErrorButton();
  }
  // Moved from the second DOMContentLoaded listener
  const container = document.getElementById('explain-error-container');
  const consoleOutput =
    document.querySelector('#out') ||
    document.querySelector('pre.console-output') ||
    document.querySelector('pre');
  if (container && consoleOutput && consoleOutput.parentNode) {
    consoleOutput.parentNode.insertBefore(container, consoleOutput);
  }

  // Add the confirmation dialog to the page
  const dialogContainer = document.getElementById('explain-error-confirm-dialog');
  if (dialogContainer && consoleOutput && consoleOutput.parentNode) {
    consoleOutput.parentNode.insertBefore(dialogContainer, consoleOutput);
  }
});

function addExplainErrorButton() {
  // First try to find the existing console button bar
  const consoleButtonBar = 
    document.querySelector('.console-actions') ||
    document.querySelector('.console-output-actions') ||
    document.querySelector('.console-controls') ||
    document.querySelector('[class*="console"][class*="button"]') ||
    document.querySelector('#console .btn-group') ||
    document.querySelector('.jenkins-button-bar');

  // Try to find buttons by their text content
  let buttonContainer = null;
  const downloadButtons = Array.from(document.querySelectorAll('a, button')).filter(el => 
    el.textContent && (
      el.textContent.includes('Download') || 
      el.textContent.includes('Copy') || 
      el.textContent.includes('View as plain text')
    )
  );

  if (downloadButtons.length > 0) {
    buttonContainer = downloadButtons[0].parentElement;
  }

  // Fallback: find console output element
  const consoleOutput =
    document.querySelector('#out') ||
    document.querySelector('pre.console-output') ||
    document.querySelector('pre');

  if (!consoleOutput && !buttonContainer) {
    console.warn('Console output element not found');
    setTimeout(addExplainErrorButton, 3000);
    return;
  }

  if (document.querySelector('.explain-error-btn')) {
    return;
  }

  const explainBtn = createButton('Explain Error', 'jenkins-button explain-error-btn', explainConsoleError);

  // If we found the button container, add our button there
  if (buttonContainer) {
    buttonContainer.insertBefore(explainBtn, buttonContainer.firstChild);
  } else if (consoleButtonBar) {
    consoleButtonBar.appendChild(explainBtn);
  } else {
    // Fallback: create a simple container above console output
    const container = document.createElement('div');
    container.className = 'explain-error-container';
    container.style.marginBottom = '10px';
    container.appendChild(explainBtn);
    consoleOutput.parentNode.insertBefore(container, consoleOutput);
  }
}

function createButton(text, className, onClick) {
  const btn = document.createElement('button');
  btn.textContent = text;
  btn.className = className;
  btn.onclick = onClick;
  return btn;
}

function explainConsoleError() {
  // First, check if an explanation already exists
  checkExistingExplanation();
}

function checkExistingExplanation() {
  const basePath = window.location.pathname.replace(/\/console$/, '');
  const url = basePath + '/console-explain-error/checkExistingExplanation';

  const headers = crumb.wrap({
    "Content-Type": "application/x-www-form-urlencoded",
  });

  fetch(url, {
    method: "POST",
    headers: headers,
    body: ""
  })
  .then(response => response.json())
  .then(data => {
    if (data.hasExplanation) {
      // Show confirmation dialog
      showConfirmationDialog(data.timestamp);
    } else {
      // No existing explanation, proceed with new request
      sendExplainRequest(false);
    }
  })
  .catch(error => {
    console.warn('Error checking existing explanation:', error);
    // If check fails, proceed with new request
    sendExplainRequest(false);
  });
}

function showConfirmationDialog(timestamp) {
  const dialog = document.getElementById('explain-error-confirm-dialog');
  const timestampSpan = document.getElementById('existing-explanation-timestamp');

  if (timestampSpan) {
    timestampSpan.textContent = timestamp;
  }

  dialog.classList.remove('jenkins-hidden');

  // Hide other elements
  hideContainer();
}

function viewExistingExplanation() {
  hideConfirmationDialog();
  sendExplainRequest(false); // This will return the cached result
}

function generateNewExplanation() {
  hideConfirmationDialog();
  sendExplainRequest(true); // Force new explanation
}

function cancelExplanation() {
  hideConfirmationDialog();
}

function hideConfirmationDialog() {
  const dialog = document.getElementById('explain-error-confirm-dialog');
  dialog.classList.add('jenkins-hidden');
}

function sendExplainRequest(forceNew = false) {
  const basePath = window.location.pathname.replace(/\/console$/, '');
  const url = basePath + '/console-explain-error/explainConsoleError';

  const headers = crumb.wrap({
    "Content-Type": "application/x-www-form-urlencoded",
  });

  // Add forceNew parameter if needed
  const body = forceNew ? "forceNew=true" : "";

  showSpinner();

  fetch(url, {
    method: "POST",
    headers: headers,
    body: body
  })
  .then(response => {
    if (!response.ok) {
      notificationBar.show('Explain failed', notificationBar.ERROR);
    }
    return response.text();
  })
  .then(responseText => {
    try {
      const jsonResponse = JSON.parse(responseText);
      showErrorExplanation(jsonResponse);
    } catch (e) {
      showErrorExplanation(responseText);
    }
  })
  .catch(error => {
    showErrorExplanation(`Error: ${error.message}`);
  });
}

function showErrorExplanation(message) {
  const container = document.getElementById('explain-error-container');
  const spinner = document.getElementById('explain-error-spinner');
  const content = document.getElementById('explain-error-content');

  container.classList.remove('jenkins-hidden');
  spinner.classList.add('jenkins-hidden');
  content.textContent = message;
}

function showSpinner() {
  const container = document.getElementById('explain-error-container');
  const spinner = document.getElementById('explain-error-spinner');
  container.classList.remove('jenkins-hidden');
  spinner.classList.remove('jenkins-hidden');
}

function hideContainer() {
  const container = document.getElementById('explain-error-container');
  container.classList.add('jenkins-hidden');
}
